import { useState } from "react";
import { Button, Card, Form, Modal, Row } from "react-bootstrap";
import { useForm } from "react-hook-form";

import { Input } from "./Input2";

export function Evento(props) {
    const {
        handleSubmit,
        register,
        formState: { errors },
    } = useForm();
    const [isUpdated, setIsUpdated] = useState(false);

    async function editEvento(data) {
        await props.editEvento({ ...data, id: props.evento.id });
        setIsUpdated(false);
    }

    return (
        <>
            <Card className="mb-3 p-3 bg-light">
                <Card.Title>
                    <strong>Nome: </strong>
                    {props.evento.nome}
                </Card.Title>
                <Card.Text>
                    <strong>Data de nacimento: </strong>
                    {props.evento.dataDeNascimento}
                </Card.Text>
               
                <Row xs="auto" className="d-flex justify-content-end">
                    <Button
                        variant="secondary"
                        onClick={() => setIsUpdated(true)}
                    >
                        Editar
                    </Button>
                    <Button
                        variant="outline-danger"
                        className="ms-3"
                        onClick={props.removeEvento}
                    >
                        Apagar
                    </Button>
                </Row>
            </Card>
            <Modal show={isUpdated} onHide={() => setIsUpdated(false)}>
                <Modal.Header>
                    <Modal.Title>
                        Editar alimento: {props.evento.nome}
                    </Modal.Title>
                </Modal.Header>
                <Form
                    noValidate
                    onSubmit={handleSubmit(editEvento)}
                    validated={!!errors}
                >
                    <Modal.Body>
                        <Input
                            className="mb-3"
                            type="text"
                            defaultValue={props.evento.nome}
                            label="Nome do Evento"
                            placeholder="Insira o nome do Evento"
                            required={true}
                            name="nameEvento"
                            error={errors.nameEvento}
                            validations={register("nameEvento", {
                                required: {
                                    value: true,
                                    message: "Nome do  é obrigatório.",
                                },
                            })}
                        />
                        <Form.Group>
                            <Form.Label>
                                Seleciona a unidade de medida
                            </Form.Label>
                            <Form.Select
                                {...register("unity")}
                                defaultValue={props.evento.unidadeMedida}
                            >
                                <option disabled>Clique para selecionar</option>
                                <option value={"Kilograma"}>Kilograma</option>
                                <option value={"Grama"}>Grama</option>
                                <option value={"Mililitro"}>Mililitro</option>
                                <option value={"Litro"}>Litro</option>
                            </Form.Select>
                        </Form.Group>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="primary" type="submit">
                            Editar
                        </Button>
                        <Button
                            variant="secondary"
                            onClick={() => setIsUpdated(false)}
                        >
                            Fechar
                        </Button>
                    </Modal.Footer>
                </Form>
            </Modal>
        </>
    );
}
