import { Container, Col, Modal, Form, Button, Row } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import { useForm } from "react-hook-form";

import "../index.css";
import { Evento } from "../components/Evento";
import { Header } from "../components/Header";
import { Input } from "../components/Input";

import {
    createEvento,
    deleteEvento,
    getEventos,
    updateEvento,
} from "../services/evento-service";

export function Eventos() {
    const [eventos, setEventos] = useState([]);
    const [isCreated, setIsCreated] = useState(false);
    const {
        handleSubmit,
        register,
        formState: { errors },
    } = useForm();
    const navigate = useNavigate();

    useEffect(() => {
        findEventos();
        // eslint-disable-next-line
    }, []);

    async function findEventos() {
        try {
            const result = await getEventos();
            setEventos(result.data);
        } catch (error) {
            console.error(error);
            navigate("/");
        }
    }

    async function removeEvento(id) {
        try {
            await deleteEvento(id);
            await findEventos();
        } catch (error) {
            console.error(error);
        }
    }

    async function addEvento(data) {
        try {
            await createEvento(data);
            setIsCreated(false);
            await findEventos();
        } catch (error) {
            console.error(error);
        }
    }

    async function editEvento(data) {
        try {
            await updateEvento({
                id: data.id,
                nameEvento: data.nameEvento,
                unity: data.unity,
            });
            await findEventos();
        } catch (error) {
            console.error(error);
        }
    }

    return (
        <Container fluid>
            <Container id="title" className="">
                <div>
                    <ul
                        id="barra"
                        class="nav-bar nav-pills nav-fill navbar  navbar-expand-md "
                    >
                        <li id="titulo" class="nav-item">
                            <a class="nav-link" href="#">
                                Inicio
                            </a>
                        </li>
                        <li id="titulo" class="nav-item">
                            <a class="nav-link" href="#">
                                Eventoes
                            </a>
                        </li>
                        <li id="titulo" class="nav-item">
                            <a class="nav-link" href="#">
                                Eventos
                            </a>
                        </li>
                        <li id="bemvindo" class="nav-item">
                            <a class="nav-link" href="#">
                                Bem vindo, homarinho
                            </a>
                        </li>
                        <li id="tituloT" class="nav-item">
                            <a class="nav-link" href="#">
                                <svg
                                    id="tradu"
                                    xmlns="http://www.w3.org/2000/svg"
                                    width="32"
                                    height="32"
                                    fill="currentColor"
                                    class="bi bi-translate"
                                    viewBox="0 0 16 16"
