            <Container>
             <Col id="botocria" md="10">
                    <Button onClick={() => setIsCreated(true)}>
                        Adicionar um novo evento
                    </Button>
                    <Button
                        className="m-1"
                        variant="outline-secondary"
                        onClick={() => {
                            sessionStorage.removeItem("token");
                            navigate("/login");
                        }}
                    >
                        Sair da pagina
                    </Button>
                </Col>

                <Col className="w-5 m-auto mb-5">
                    {eventos && eventos.length > 0 ? (
                        eventos.map((evento, index) => (
                            <Evento
                                key={index}
                                evento={evento}
                                removeEvento={async () =>
                                    await removeEvento(evento.id)
                                }
                                editEvento={editEvento}
                            />
                        ))
                    ) : (
                        <p className="text-center">
                            Não existe nenhum evento cadastrado!
                        </p>
                    )}
                </Col>
                {/* Formulário dentro do Modal, ideal seria componentizar também, pois é parecido com o Modal de editar */}
                <Modal show={isCreated} onHide={() => setIsCreated(false)}>
                    <Modal.Header>
                        <Modal.Title>Cadastrar novo evento</Modal.Title>
                    </Modal.Header>
                    <Form
                        noValidate
                        onSubmit={handleSubmit(addEvento)}
                        validated={!!errors}
                    >
                        <Modal.Body>
                            <Input
                                className="mb-3"
                                type="text"
                                label="Nome do evento"
                                placeholder="Insira o nome do evento"
                                required={true}
                                name="nameEvento"
                                error={errors.nameEvento}
                                validations={register("nameEvento", {
                                    required: {
                                        value: true,
                                        message:
                                            "Nome do evento é obrigatório.",
                                    },
                                })}
                            />
                            <Form.Group className="mb-3">
                                <Form.Label>
                                    Seleciona a unidade de medida
                                </Form.Label>
                                <Form.Select {...register("unity")}>
                                    <option disabled>
                                        Clique para selecionar
                                    </option>
                                    <option value={"Kilograma"}>SP</option>
                                    <option value={"Grama"}>MG</option>
                                    <option value={"Mililitro"}>MT</option>
                                    <option value={"Litro"}>RS</option>
                                </Form.Select>
                            </Form.Group>
                        </Modal.Body>
                        <Modal.Footer>
                            <Button variant="primary" type="submit">
                                Criar
                            </Button>
                            <Button
                                variant="secondary"
                                onClick={() => setIsCreated(false)}
                            >
                                Fechar
                            </Button>
                        </Modal.Footer>
                    </Form>
                </Modal>
