import { api } from "./api";

export async function getEventos() {
    const accessToken = sessionStorage.getItem('token');
    const result = await api.get('/eventos', {
        headers: {
            'Authorization': `Bearer ${JSON.parse(accessToken)}`
        }
    });
    return result;
}

export async function deleteGestor(id) {
    const accessToken = sessionStorage.getItem('token');
    const result = await api.delete(`/evento/${id}`, {
        headers: {
            'Authorization': `Bearer ${JSON.parse(accessToken)}`
        }
    });
    return result;
}

export async function updateGestor(data) {
    const accessToken = sessionStorage.getItem('token');
    const result = await api.put(`/evento/${data.id}`, {
        nome: data.unity,
        dataDeNascimento: data.unity,

    }, {
        headers: {
            'Authorization': `Bearer ${JSON.parse(accessToken)}`
        }
    });
    return result;
}

export async function createGestor(data) {
    const accessToken = sessionStorage.getItem('token');
    const result = await api.post('/evento', {
        nome: data.unity,
        dataDeNascimento: data.unity,

    }, {
        headers: {
            'Authorization': `Bearer ${JSON.parse(accessToken)}`
        }
    });
    return result;
}
